// pages/user/user.js
const app = getApp()
const http = require("../../utils/httputils")
import { requstUrl } from "../../utils/requestUrl"
import Toast from '../../miniprogram/miniprogram_npm/vant-weapp/toast/toast';
Page({
  /**
   * 页面的初始数据
   */
  data: {
    cells: [
      {
        icon: "data:image/svg+xml;base64,",
        name: "案件列表",
        path: "/pages/cases/cases"
      },
      {
        icon: "data:image/svg+xml;base64,",
        name: "律师资料",
        path: "/pages/authentication/authentication"
      },
      // {
      //   icon: "data:image/svg+xml;base64,",
      //   name: "收支明细",
      //   path: ""
      // },
      // {
      //   icon: "data:image/svg+xml;base64,",
      //   name: "推送通知",
      //   path: ""
      // },
      // {
      //   icon: "data:image/svg+xml;base64,",
      //   name: "关于我们",
      //   path: ""
      // },
      // {
      //   icon: "data:image/svg+xml;base64,",
      //   name: "客服中心",
      //   path: ""
      // },
      // {
      //   icon: "data:image/svg+xml;base64,",
      //   name: "设置",
      //   path: ""
      // },
    ],
    //律师信息
    lawInfo: {},
    user: {},
    isIPhoneX: false,
    token: wx.getStorageSync('token7'),
    textval: "获取验证码",
    codeTime: 60,
    phone: "",
    code: "",
    codeType: false,
    timer: "",
    isPhone: false
  },
  /**
   * 生命周期函数--监听页面加载
   */
  onLoad: function (options) {
    if (app.globalData.lawInfo) {
      this.setData({
        lawInfo: app.globalData.lawInfo,
        isIPhoneX: app.globalData.isIPhoneX
      })
      this.getUser()
    } else {
      app.userInfoReadyCallback = res => {
        this.setData({
          lawInfo: res.lawInfo,
          isIPhoneX: res.isIPhoneX,
          token: res.token
        })
        let that = this
        wx.request({
          url: requstUrl.getUserInfo,
          data: {},
          header: {
            'content-type': 'application/json',
            usertokey: res.token
          },
          success(res) {
            that.setData({
              user: res.data.data
            })
            if (res.data.data == null) {
              return false
            }
            if (res.data.data.phone_number == '' || res.data.data.phone_number == null) {
              that.setData({
                isPhone: true
              })
            }
          }
        })
      }
    }
  },
  /**
   * 用户点击右上角分享
   */
  onShareAppMessage: function () {

  },
  //小程序显示
  onShow() {
    this.getLawInfo()
  },
  goHome(e) {
    wx.navigateTo({
      url: '/pages/index/index',
    })
  },
  //获取律师认证详情
  getLawInfo() {
    let that = this
    return new Promise((resolve, reject) => {
      http.getRequest(requstUrl.getlawInfo, {}, function (res) {
        that.setData({
          lawInfo: res
        })
        resolve(res)
      })
    })
  },
  toInfo(e) {
    let path = e.currentTarget.dataset.path
    let code = this.data.lawInfo.code
    if (path == '/pages/authentication/authentication') {
      if (code == 2 || code == -1) {
        wx.navigateTo({
          url: path,
        })
      } else {
        wx.navigateTo({
          url: '/pages/examine/examine?type=' + code
        })
      }
    } else {
      wx.navigateTo({
        url: path,
      })
    }
  },
  //获取用户信息
  getUser() {
    let that = this
    wx.request({
      url: requstUrl.getUserInfo,
      data: {},
      header: {
        'content-type': 'application/json',
        usertokey: app.globalData.token
      },
      success(res) {
        that.setData({
          user: res.data.data
        })
        if (res.data.data == null) {
          return false
        }
        if (res.data.data.phone_number == '' || res.data.data.phone_number == null) {
          that.setData({
            isPhone: true
          })
        }
      }
    })
  },
  //认证
  authentication(e) {
    let code = this.data.lawInfo.code
    console.log(code);
    if (code == -1) { //未认证
      wx.navigateTo({
        url: '/pages/authentication/authentication'
      })
    } else if (code == 2) { //认证通过
      wx.navigateTo({
        url: '/pages/authentication/authentication'
      })
    } else { //待审核 或 未通过
      wx.navigateTo({
        url: '/pages/examine/examine?type=' + code
      })
    }
  },
  //输入框离开事件
  outInput(e) {
    let name = e.currentTarget.dataset.name
    console.log(e.detail.value);
    this.setData({
      [name]: e.detail.value
    })
  },
  //获取手机验证码
  getPhoneCode() {
    let that = this
    if (this.data.phone != '' && this.data.codeType == false) {
      let code = that.data.codeTime
      that.setData({
        codeType: true,
        timer: setInterval(() => {
          if (that.data.codeTime > 0) {
            code--
            that.setData({
              codeTime: code,
              textval: "请等待" + code + "秒",
            })
          } else {
            clearInterval(that.data.timer);
            that.setData({
              textval: "获取验证码",
              codeType: false,
              timer: ""
            })
          }
        }, 1000)
      })
      wx.request({
        url: requstUrl.getPhoneCode,
        data: {
          phone_number: this.data.phone
        },
        method: 'GET',
        header: {
          'content-type': 'application/json'
        },
        success: function (res) {
          if (res.data != null && res.data.state) {
            //
          } else {
            Toast('请勿频繁发送')
          }
        }
      })
    }
  },
  //保存会员手机号
  saveUserPhone() {
    let that = this
    wx.request({
      url: requstUrl.saveUserPhone,
      data: {
        phone_number: this.data.phone,
        sms_code: this.data.code
      },
      method: 'GET',
      header: {
        'content-type': 'application/json',
        usertokey: app.globalData.token
      },
      success: function (res) {
        if (res.data.state) {
          that.setData({
            isPhone: false
          })
          if (res.data.data) {
            Toast('您已是债主帮认证律师，您在债主帮其他移动律师端的认证及接案等信息将会被迁移至此。');
          } else {
            Toast(res.data.msg)
          }
        } else {
          Toast.fail('绑定失败');
        }
      }
    })
  }
})